mapper.select:
SELECT g.* FROM `group` g
JOIN group_member gm ON g.id = gm.group_id
WHERE gm.user_id = #{userId}

SELECT COUNT(*) FROM `group` g
WHERE g.group_name LIKE CONCAT('%', #{keyword}, '%')

    AND DATE(g.create_time) >= #{startDate}


    AND DATE(g.create_time) <= #{endDate}

SELECT g.* FROM `group` g
WHERE g.group_name LIKE CONCAT('%', #{keyword}, '%')

    AND DATE(g.create_time) >= #{startDate}


    AND DATE(g.create_time) <= #{endDate}


    AND g.group_type IN

        #{groupType}



    AND g.is_public = #{isPublic}

ORDER BY


        g.create_time ASC, g.id ASC


        g.create_time DESC, g.id DESC


        g.group_name ASC, g.id ASC


        g.group_name DESC, g.id DESC


        g.create_time DESC, g.id DESC


LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM `group` g
WHERE g.group_name LIKE CONCAT('%', #{keyword}, '%')

    AND DATE(g.create_time) >= #{startDate}


    AND DATE(g.create_time) <= #{endDate}


    AND g.group_type IN

        #{groupType}



    AND g.is_public = #{isPublic}
