       OR u.nickname LIKE CONCAT('%', #{keyword}, '%'))

    AND DATE(u.registration_date) >= #{startDate}


    AND DATE(u.registration_date) <= #{endDate}

ORDER BY ${sortClause}
LIMIT #{offset}, #{limit}

