SELECT id, username, sex, phone_number, user_email, unique_article_link, unique_video_link, registration_date, ip_ownership, type, avatar, nickname, badge, login_time, bio, online_status FROM user u WHERE (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%'))
AND DATE(u.registration_date) >= #{startDate}
AND DATE(u.registration_date) <= #{endDate}
AND u.type IN
#{userType}
AND u.online_status = #{onlineStatus}
ORDER BY
u.registration_date ASC, u.id ASC
u.registration_date DESC, u.id DESC
u.username ASC, u.id ASC
u.username DESC, u.id DESC
u.registration_date DESC, u.id DESC
LIMIT #{offset}, #{limit}

