SELECT u.id, u.username, u.password, u.sex, u.phone_number, u.user_email, u.unique_article_link, u.unique_video_link, u.registration_date, u.ip_ownership, u.type, u.avatar, u.nickname, u.badge, u.login_time, u.bio, u.online_status, us.user_id as stats_user_id, us.fans_count, us.total_likes, us.total_favorites, us.total_sponsorship, us.total_article_exposure, us.website_coins, us.created_at as stats_created_at, us.updated_at as stats_updated_at FROM user u LEFT JOIN user_stats us ON u.id = us.user_id WHERE (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%'))
AND u.status = #{status}
ORDER BY CASE WHEN u.username = #{keyword} THEN 1 WHEN u.nickname = #{keyword} THEN 2 WHEN u.username LIKE CONCAT(#{keyword}, '%') THEN 3 WHEN u.nickname LIKE CONCAT(#{keyword}, '%') THEN 4 ELSE 5 END, u.id DESC LIMIT #{offset}, #{pageSize}

SELECT u.id, u.username, u.password, u.sex, u.phone_number, u.user_email, u.unique_article_link, u.unique_video_link, u.registration_date, u.ip_ownership, u.type, u.avatar, u.nickname, u.badge, u.login_time, u.bio, u.online_status, us.user_id as stats_user_id, us.fans_count, us.total_likes, us.total_favorites, us.total_sponsorship, us.total_article_exposure, us.website_coins, us.created_at as stats_created_at, us.updated_at as stats_updated_at FROM user u LEFT JOIN user_stats us ON u.id = us.user_id
AND u.status = #{status}
ORDER BY u.id DESC LIMIT #{offset}, #{pageSize}
