mapper.select:
SELECT u.id, u.username, u.sex, u.phone_number, u.user_email, u.unique_article_link, u.unique_video_link, u.registration_date, u.ip_ownership, u.type, u.avatar, u.nickname, u.badge, u.login_time, u.bio, u.online_status, us.user_id as stats_user_id, us.fans_count, us.total_likes, us.total_favorites, us.total_sponsorship, us.total_article_exposure, us.website_coins, us.created_at as stats_created_at, us.updated_at as stats_updated_at FROM user u LEFT JOIN user_stats us ON u.id = us.user_id
AND u.type = #{type}
AND u.online_status = #{onlineStatus}
AND u.username LIKE CONCAT('%', #{username}, '%')
AND us.fans_count >= #{minFansCount}
AND us.total_likes >= #{minTotalLikes}
ORDER BY
us.fans_count DESC
us.total_likes DESC
u.registration_date DESC
u.login_time DESC
u.type DESC, u.id DESC

SELECT COUNT(*) FROM user


        AND type = #{type}


        AND online_status = #{onlineStatus}
